	builders := make([]*StreamCheckpointCreate, rv.Len())
	for i := 0; i < rv.Len(); i++ {
		builders[i] = c.Create()
		setFunc(builders[i], i)
	}
	return &StreamCheckpointCreateBulk{config: c.config, builders: builders}
